import { createStore } from 'vuex';

const store =  createStore({
    state() {
        return {
          token: ''
        };
      },
      mutations: {
        auth (state, accessToken) {
          // console.log("aa");
          state.token = accessToken
        }
      },
      actions: {
        login (context, adminData) {
          context.commit('auth',adminData.data.accessToken)
        }
      }
    });
 
export default store;
