import Axios from "axios";
import { adminLogin } from "@/api/admin.js";
import VueCookies from 'vue-cookies'
import Swal from "sweetalert2";

export default {
  async login(context, payload) {

    const response = await Axios.post(adminLogin, payload);

    if (!response.data.success) {
      Swal.fire({
        title: "Login",
        text: "login fail",
        icon: "danger",
      });
      return response.data.success
    }

    VueCookies.set('cookie_token', response.data.accessToken);
    context.commit('auth', response.data.accessToken)
    return response.data.success
  }
};
