import Axios from "axios";
import { adminLogin } from "@/api/admin.js";

export default {
  async login(context, payload) {

    try {
      const response = await Axios.post(adminLogin, payload);

      context.commit('auth', response.data.accessToken)
      return response.data.success
    } catch (error) {
      return error
    }


  }
};
