import { createStore } from 'vuex';

const store =  createStore({
    state() {
        return {
          token: '',
          adminId: ''
        };
      },
      mutations: {
        auth (state, adminData) {
          state.token = adminData.token,
          state.adminId = adminData.adminId
        }
      },
      actions: {
        login (context, adminData) {
          context.commit('auth',adminData)
        }
      }
    });
 
export default store;
